import React from "react";
import { NavLink, useNavigate } from "react-router-dom";

function Sidebar({ handleLogout }) {
  const navigate = useNavigate();

  const logout = () => {
    handleLogout();
    navigate("/login");
  };

  return (
    <nav className="fixed top-0 left-0 h-screen w-64 bg-white rounded-r-3xl shadow-lg flex flex-col justify-between py-6 px-4 overflow-auto">
      {/* Top: Logo */}
      <div>
        <div className="mb-8 text-center">
          <img src="/logotype.png" alt="logo" className="w-36 mx-auto" />
        </div>

        {/* Navigation Links */}
        <ul className="space-y-3">
          <li>
            <NavLink
              to="/dashboard"
              className={({ isActive }) =>
                `flex items-center px-4 py-2 rounded text-gray-800 font-medium hover:bg-gray-100 transition-all ${
                  isActive ? "bg-gray-200" : ""
                }`
              }
            >
              Dashboard
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/appointments"
              className={({ isActive }) =>
                `flex items-center px-4 py-2 rounded text-gray-800 font-medium hover:bg-gray-100 transition-all ${
                  isActive ? "bg-gray-200" : ""
                }`
              }
            >
              Appointments
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/learn"
              className={({ isActive }) =>
                `flex items-center px-4 py-2 rounded text-gray-800 font-medium hover:bg-gray-100 transition-all ${
                  isActive ? "bg-gray-200" : ""
                }`
              }
            >
              Learn
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/games"
              className={({ isActive }) =>
                `flex items-center px-4 py-2 rounded text-gray-800 font-medium hover:bg-gray-100 transition-all ${
                  isActive ? "bg-gray-200" : ""
                }`
              }
            >
              Play
            </NavLink>
          </li>
        </ul>
      </div>

      {/* Bottom: Doctor Image + Logout */}
      <div className="flex flex-col items-center">
        <img
          src="/assets/images/doctor_sidebar.png"
          alt="Doctor"
          className="w-40 mb-6"
        />
      </div>
    </nav>
  );
}

export default Sidebar;
