import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Appointments = () => {
  const [appointments, setAppointments] = useState([]);

  const storedUser = localStorage.getItem("user");
  const userid = storedUser ? JSON.parse(storedUser).id : null;

  useEffect(() => {
    if (!userid) return;

    axios
      .get(`http://localhost:5001/api/appointments/${userid}`)
      .then((res) => setAppointments(res.data))
      .catch((err) => console.error("Appointments error:", err));
  }, [userid]);

  return (
    <main className="min-h-screen bg-slate-100 p-4 md:p-8">
      {/* HEADER */}
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-3xl font-bold text-gray-800">üìÖ My Appointments</h2>

        <Link to="/dashboard" className="text-sm text-blue-600 hover:underline">
          ‚Üê Back to Dashboard
        </Link>
      </div>

      {/* EMPTY STATE */}
      {appointments.length === 0 ? (
        <div className="bg-white rounded-2xl p-8 shadow text-center">
          <p className="text-xl mb-2">üéà No appointments yet</p>
          <p className="text-gray-500">
            When you have one, it will appear here!
          </p>
        </div>
      ) : (
        <div className="grid gap-6 md:grid-cols-2">
          {appointments.map((appt) => (
            <div
              key={appt.id}
              className="bg-white rounded-2xl p-6 shadow hover:scale-[1.01] transition-transform"
            >
              {/* TOP */}
              <div className="flex items-center justify-between mb-3">
                <span className="px-3 py-1 rounded-full text-sm bg-blue-100 text-blue-700">
                  {appt.department}
                </span>

                <span className="text-sm text-gray-400">
                  üïí {appt.appointment_time}
                </span>
              </div>

              {/* MAIN */}
              <h3 className="text-lg font-bold text-gray-800 mb-1">
                {new Date(appt.appointment_date).toLocaleDateString()}
              </h3>

              <p className="text-gray-600 mb-2">üë©‚Äç‚öïÔ∏è {appt.doctor_name}</p>

              <p className="text-sm text-gray-500 mb-3">üìç {appt.location}</p>

              {/* DESCRIPTION */}
              {appt.description && (
                <div className="bg-slate-50 rounded-xl p-3 text-sm text-gray-600">
                  üí¨ {appt.description}
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </main>
  );
};

export default Appointments;
