import React, { useEffect, useState } from "react";

const Appointments = () => {
  const [appointments, setAppointments] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (!storedUser) return;

    const parsedUser = JSON.parse(storedUser);
    setUser(parsedUser);

    fetch(`http://localhost:5001/api/appointments/${parsedUser.id}`)
      .then((res) => res.json())
      .then(setAppointments)
      .catch((err) => console.error("Appointments fetch error:", err));
  }, []);

  return (
    <main className="min-h-screen bg-slate-100 p-4 md:p-8">
      <h1 className="text-3xl font-bold mb-6">My Appointments</h1>

      {appointments.length === 0 ? (
        <p className="text-gray-500">No upcoming appointments.</p>
      ) : (
        <div className="grid gap-6 md:grid-cols-2">
          {appointments.map((appt) => (
            <div key={appt.id} className="bg-white rounded-2xl p-6 shadow">
              <h2 className="text-xl font-semibold text-[#015CE9]">
                {appt.department}
              </h2>

              <p className="text-gray-600 mt-1">
                Doctor:{" "}
                <span className="font-medium">{appt.doctor_name || "‚Äî"}</span>
              </p>

              <p className="text-gray-600">üìç {appt.location}</p>

              <p className="mt-3 font-medium">
                üóì {appt.appointment_date} ‚è∞ {appt.appointment_time}
              </p>

              {appt.description && (
                <p className="mt-3 text-gray-500 text-sm">{appt.description}</p>
              )}
            </div>
          ))}
        </div>
      )}
    </main>
  );
};

export default Appointments;
