import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

import Sidebar from "./components/Sidebar";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Map from "./pages/Map";
import Dashboard from "./pages/Dashboard";
import Account from "./pages/dashboard/Account";
import Games from "./pages/dashboard/Games";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogin = (userData) => {
    localStorage.setItem("user", JSON.stringify(userData));
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setIsLoggedIn(false);
    setIsSidebarOpen(false);
  };

  return (
    <Router>
      <div className="flex min-h-screen">
        {/* Mobile hamburger */}
        {isLoggedIn && (
          <button
            onClick={() => setIsSidebarOpen(true)}
            className="md:hidden fixed top-4 left-4 z-50 bg-[#015CE9] text-white p-2 rounded-lg shadow-lg"
          >
            â˜°
          </button>
        )}

        {/* Sidebar */}
        {isLoggedIn && (
          <Sidebar
            isOpen={isSidebarOpen}
            closeSidebar={() => setIsSidebarOpen(false)}
            handleLogout={handleLogout}
          />
        )}

        {/* Main content */}
        <main className="flex-1 min-h-screen md:ml-64">
          <Routes>
            <Route
              path="/"
              element={
                isLoggedIn ? (
                  <Navigate to="/dashboard" replace />
                ) : (
                  <Navigate to="/login" replace />
                )
              }
            />

            <Route
              path="/login"
              element={
                isLoggedIn ? (
                  <Navigate to="/dashboard" />
                ) : (
                  <Login handleLogin={handleLogin} />
                )
              }
            />

            <Route
              path="/register"
              element={isLoggedIn ? <Navigate to="/dashboard" /> : <Register />}
            />

            <Route
              path="/dashboard"
              element={isLoggedIn ? <Dashboard /> : <Navigate to="/login" />}
            />
            <Route
              path="/account"
              element={isLoggedIn ? <Account /> : <Navigate to="/login" />}
            />
            <Route
              path="/games"
              element={isLoggedIn ? <Games /> : <Navigate to="/login" />}
            />
            <Route
              path="/map"
              element={isLoggedIn ? <Map /> : <Navigate to="/login" />}
            />

            <Route
              path="*"
              element={
                isLoggedIn ? (
                  <Navigate to="/dashboard" />
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
