import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

/* -----------------------------------
   Interactive checklist component
----------------------------------- */
const PreparationChecklist = ({ appointmentId }) => {
  const storageKey = `checklist_appointment_${appointmentId}`;

  const checklistItems = [
    { id: "card", label: "Bring hospital card", icon: "ü™™" },
    { id: "medicine", label: "Bring medicines", icon: "üíä" },
    { id: "toy", label: "Bring favorite toy", icon: "üß∏" },
    { id: "water", label: "Drink some water", icon: "üíß" },
  ];

  const [checkedItems, setCheckedItems] = useState({});

  useEffect(() => {
    const saved = localStorage.getItem(storageKey);
    if (saved) setCheckedItems(JSON.parse(saved));
  }, [storageKey]);

  const toggleItem = (id) => {
    const updated = { ...checkedItems, [id]: !checkedItems[id] };
    setCheckedItems(updated);
    localStorage.setItem(storageKey, JSON.stringify(updated));
  };

  return (
    <div className="mt-4 bg-purple-50 rounded-xl p-4">
      <h4 className="font-semibold text-purple-700 mb-3">
        üß† Prepare for your visit
      </h4>

      <ul className="space-y-2">
        {checklistItems.map((item) => (
          <li
            key={item.id}
            onClick={() => toggleItem(item.id)}
            className={`flex items-center gap-3 cursor-pointer p-2 rounded-lg transition
              ${
                checkedItems[item.id]
                  ? "bg-purple-200 text-purple-900 line-through"
                  : "hover:bg-purple-100 text-purple-800"
              }
            `}
          >
            <span className="text-xl">{item.icon}</span>
            <span className="text-sm flex-1">{item.label}</span>
            {checkedItems[item.id] && <span>‚úÖ</span>}
          </li>
        ))}
      </ul>

      {Object.values(checkedItems).filter(Boolean).length ===
        checklistItems.length && (
        <p className="mt-3 text-sm text-purple-700 font-medium">
          üåü Great job! You are all ready!
        </p>
      )}
    </div>
  );
};

/* -----------------------------------
   Helper: Safe date + time formatting
----------------------------------- */
const formatAppointmentDateTime = (dateStr, timeStr) => {
  if (!dateStr) return "No date";

  // Combine date + time with "T" to ensure valid ISO format
  const dateTimeString = timeStr
    ? `${dateStr}T${timeStr}`
    : `${dateStr}T00:00:00`;
  const dateObj = new Date(dateTimeString);

  if (isNaN(dateObj)) return "Invalid date";

  // Format nicely: Jan 8, 2026, 10:30 AM
  const options = {
    year: "numeric",
    month: "short",
    day: "numeric",
    hour: "numeric",
    minute: "2-digit",
  };

  return dateObj.toLocaleString(undefined, options);
};

/* -----------------------------------
   Appointments page
----------------------------------- */
const Appointments = () => {
  const [appointments, setAppointments] = useState([]);
  const storedUser = localStorage.getItem("user");
  const userid = storedUser ? JSON.parse(storedUser).id : null;

  useEffect(() => {
    if (!userid) return;

    axios
      .get(`http://localhost:5001/api/appointments/${userid}`)
      .then((res) => setAppointments(res.data))
      .catch((err) => console.error("Appointments error:", err));
  }, [userid]);

  return (
    <main className="min-h-screen bg-slate-100 p-4 md:p-8">
      {/* HEADER */}
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-3xl font-bold text-gray-800">üìÖ My Appointments</h2>
        <Link to="/dashboard" className="text-sm text-blue-600 hover:underline">
          ‚Üê Back to Dashboard
        </Link>
      </div>

      {/* EMPTY STATE */}
      {appointments.length === 0 ? (
        <div className="bg-white rounded-2xl p-8 shadow text-center">
          <p className="text-xl mb-2">üéà No appointments yet</p>
          <p className="text-gray-500">
            When you have one, it will appear here!
          </p>
        </div>
      ) : (
        <div className="grid gap-6 md:grid-cols-2">
          {appointments.map((appt) => (
            <div
              key={appt.id}
              className="bg-white rounded-2xl p-6 shadow hover:scale-[1.01] transition-transform"
            >
              {/* TOP */}
              <div className="flex items-center justify-between mb-3">
                <span className="px-3 py-1 rounded-full text-sm bg-blue-100 text-blue-700">
                  {appt.department || "No department"}
                </span>

                <span className="text-sm text-gray-400">
                  üïí {appt.appointment_time || "--:--"}
                </span>
              </div>

              {/* MAIN */}
              <h3 className="text-lg font-bold text-gray-800 mb-1">
                {formatAppointmentDateTime(
                  appt.appointment_date,
                  appt.appointment_time
                )}
              </h3>

              <p className="text-gray-600 mb-2">
                üë©‚Äç‚öïÔ∏è {appt.doctor_name || "Unknown Doctor"}
              </p>
              <p className="text-sm text-gray-500 mb-3">
                üìç {appt.location || "No location"}
              </p>

              {/* DESCRIPTION */}
              {appt.description && (
                <div className="bg-slate-50 rounded-xl p-3 text-sm text-gray-600 mb-3">
                  üí¨ {appt.description}
                </div>
              )}

              {/* INTERACTIVE CHECKLIST */}
              <PreparationChecklist appointmentId={appt.id} />
            </div>
          ))}
        </div>
      )}
    </main>
  );
};

export default Appointments;
