import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [buddy, setBuddy] = useState(null);
  const [appointments, setAppointments] = useState([]);
  const [medicines, setMedicines] = useState([]);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    const storedBuddy = localStorage.getItem("buddy");

    if (storedUser) setUserData(JSON.parse(storedUser));
    if (storedBuddy) setBuddy(JSON.parse(storedBuddy));

    fetch("http://localhost:5001/api/appointments")
      .then((res) => res.json())
      .then(setAppointments)
      .catch(console.error);

    fetch("http://localhost:5001/api/medicines")
      .then((res) => res.json())
      .then(setMedicines)
      .catch(console.error);
  }, []);

  if (!userData) return <div className="p-8">Loading...</div>;

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  return (
    <main className="min-h-screen flex bg-slate-100">
      {/* Sidebar */}
      <aside className="w-64 bg-white rounded-r-3xl shadow-lg flex flex-col justify-between p-6 hidden sm:flex">
        {/* Top */}
        <div className="space-y-10">
          {/* Logo */}
          <div className="text-center">
            <img src="/logotype.png" alt="logo" className="w-32 mx-auto mb-6" />
          </div>

          {/* Links */}
          <nav className="flex flex-col space-y-4">
            <Link
              to="/dashboard"
              className="text-gray-700 font-medium hover:text-blue-600"
            >
              Dashboard
            </Link>
            <Link
              to="/appointments"
              className="text-gray-700 font-medium hover:text-blue-600"
            >
              Appointments
            </Link>
            <Link
              to="/learn"
              className="text-gray-700 font-medium hover:text-blue-600"
            >
              Learn
            </Link>
            <Link
              to="/games"
              className="text-gray-700 font-medium hover:text-blue-600"
            >
              Play
            </Link>
            <button
              onClick={handleLogout}
              className="text-red-500 font-medium text-left"
            >
              Logout
            </button>
          </nav>
        </div>

        {/* Bottom doctor image */}
        <div className="mt-10 text-center">
          <img
            src="/assets/images/doctor_sidebar.png"
            alt="Doctor"
            className="w-40 mx-auto"
          />
        </div>
      </aside>

      {/* Main content */}
      <section className="flex-1 p-4 sm:p-8 grid gap-6 grid-cols-1 md:grid-cols-4">
        {/* Header */}
        <div className="md:col-span-3 bg-white rounded-2xl p-6 flex items-center gap-6 shadow flex-wrap">
          {buddy ? (
            <img
              src={`/assets/images/icons/${buddy.icon_filename}`}
              alt={buddy.name}
              className="w-20 h-20"
            />
          ) : (
            <div className="w-20 h-20 bg-gray-200 rounded-full" />
          )}

          <div>
            <h2 className="text-2xl font-bold">Hi, {userData.firstName} üëã</h2>
            {buddy && (
              <p className="text-sm text-gray-500">
                Your hospital buddy:{" "}
                <span className="font-medium">{buddy.name}</span>
              </p>
            )}
          </div>
        </div>

        {/* Mood */}
        <div className="bg-purple-500 rounded-2xl p-6 text-white flex flex-col items-center">
          <h3 className="text-xl mb-6">How are you feeling today?</h3>
          <div className="grid grid-cols-2 gap-4 text-3xl">
            üòÄ ü§ï üò¥ üò° üòÅ ü§¢
          </div>
        </div>

        {/* Appointment */}
        <div className="bg-white rounded-2xl p-6 shadow">
          <h4 className="font-bold text-gray-700">Next Appointment</h4>
          {appointments[0] ? (
            <>
              <p className="text-xl mt-2">{appointments[0].title}</p>
              <p className="text-gray-500">
                {appointments[0].date} ‚Äì {appointments[0].time}
              </p>
            </>
          ) : (
            <p className="text-gray-400">No appointments</p>
          )}
        </div>

        {/* Medicine */}
        <div className="bg-white rounded-2xl p-6 shadow">
          <h4 className="font-bold text-gray-700">My Medicines</h4>
          {medicines[0] ? (
            <>
              <p className="text-xl mt-2">{medicines[0].time}</p>
              <p className="text-gray-500">
                {medicines[0].name} {medicines[0].dosage}
              </p>
            </>
          ) : (
            <p className="text-gray-400">No medicines scheduled</p>
          )}
        </div>

        {/* Navigation */}
        <Link
          to="/map"
          className="bg-purple-400 text-white rounded-2xl p-6 shadow"
        >
          <h3 className="text-xl font-bold">Hospital Map</h3>
        </Link>

        <Link
          to="/learn"
          className="bg-purple-400 text-white rounded-2xl p-6 shadow"
        >
          <h3 className="text-xl font-bold">Learn About Hospital</h3>
        </Link>

        <Link
          to="/games"
          className="col-span-1 md:col-span-3 bg-orange-500 text-white rounded-2xl p-6 flex justify-between items-center shadow"
        >
          <h3 className="text-3xl font-bold">Play Zone</h3>
          <span className="text-2xl">üéÆ</span>
        </Link>
      </section>
    </main>
  );
};

export default Dashboard;
