import React, { useEffect, useState } from "react";
import axios from "axios";

const Appointments = () => {
  const [appointments, setAppointments] = useState([]);

  // ✅ Read user from storage
  const storedUser = localStorage.getItem("user");
  const userid = storedUser ? JSON.parse(storedUser).id : null;

  useEffect(() => {
    console.log("Appointments userid:", userid);

    if (!userid) return;

    axios
      .get(`http://localhost:5001/api/appointments/${userid}`)
      .then((res) => {
        console.log("Appointments data:", res.data);
        setAppointments(res.data);
      })
      .catch((err) => console.error("Appointments error:", err));
  }, [userid]);

  return (
    <div>
      <h2>Your Appointments</h2>

      {appointments.length === 0 ? (
        <p>No appointments found.</p>
      ) : (
        <ul>
          {appointments.map((appt) => (
            <li key={appt.id}>
              <strong>{appt.department}</strong> with {appt.doctor_name}
              <br />
              {appt.location}
              <br />
              {new Date(appt.appointment_date).toLocaleDateString()} at{" "}
              {appt.appointment_time}
              <br />
              {appt.description}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Appointments;
