import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const Dashboard = () => {
  const [userData, setUserData] = useState(null);
  const [buddy, setBuddy] = useState(null);
  const [appointments, setAppointments] = useState([]);
  const [medicines, setMedicines] = useState([]);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    const storedBuddy = localStorage.getItem("buddy");

    if (storedUser) setUserData(JSON.parse(storedUser));
    if (storedBuddy) setBuddy(JSON.parse(storedBuddy));

    fetch("http://localhost:5001/api/appointments")
      .then((res) => res.json())
      .then(setAppointments)
      .catch(console.error);

    fetch("http://localhost:5001/api/medicines")
      .then((res) => res.json())
      .then(setMedicines)
      .catch(console.error);
  }, []);

  if (!userData) {
    return <div className="p-6">Loading...</div>;
  }

  return (
    <main className="min-h-screen bg-slate-100 p-4 md:p-8">
      <section className="grid grid-cols-1 md:grid-cols-4 gap-6">
        {/* HEADER */}
        <div className="md:col-span-3 bg-white rounded-2xl p-6 shadow flex flex-col sm:flex-row items-center gap-6">
          {buddy ? (
            <img
              src={`/assets/images/icons/${buddy.icon_filename}`}
              alt={buddy.name}
              className="w-28 h-28 sm:w-40 sm:h-40 object-contain"
            />
          ) : (
            <div className="w-28 h-28 sm:w-40 sm:h-40 bg-orange-200 rounded-full" />
          )}

          <div className="text-center sm:text-left">
            <h2 className="text-2xl sm:text-4xl font-bold">
              Hi, {userData.firstName} üëã
            </h2>

            {buddy && (
              <p className="text-sm text-gray-500 mt-1">
                Your hospital buddy:{" "}
                <span className="font-medium">{buddy.name}</span>
              </p>
            )}
          </div>
        </div>

        {/* MOOD */}
        <div className="bg-purple-500 rounded-2xl p-6 text-white flex flex-col items-center">
          <h3 className="text-lg font-semibold mb-4 text-center">
            How are you feeling today?
          </h3>

          <div className="grid grid-cols-3 gap-4 text-3xl">
            <button>üòÄ</button>
            <button>ü§ï</button>
            <button>üò¥</button>
            <button>üò°</button>
            <button>üòÅ</button>
            <button>ü§¢</button>
          </div>
        </div>

        {/* APPOINTMENT */}
        <div className="bg-white rounded-2xl p-6 shadow">
          <h4 className="font-bold text-gray-700">Next Appointment</h4>

          {appointments[0] ? (
            <>
              <p className="text-lg mt-2">{appointments[0].title}</p>
              <p className="text-gray-500 text-sm">
                {appointments[0].appointment_date} ‚Äì{" "}
                {appointments[0].appointment_time}
              </p>
            </>
          ) : (
            <p className="text-gray-400 mt-2">No appointments</p>
          )}
        </div>

        {/* MEDICINE */}
        <div className="bg-white rounded-2xl p-6 shadow">
          <h4 className="font-bold text-gray-700">My Medicines</h4>

          {medicines[0] ? (
            <>
              <p className="text-lg mt-2">{medicines[0].time}</p>
              <p className="text-gray-500 text-sm">
                {medicines[0].name} {medicines[0].dosage}
              </p>
            </>
          ) : (
            <p className="text-gray-400 mt-2">No medicines scheduled</p>
          )}
        </div>

        {/* LINKS */}
        <Link
          to="/map"
          className="bg-purple-400 text-white rounded-2xl p-6 shadow hover:scale-[1.02] transition-transform"
        >
          <h3 className="text-xl font-bold">Hospital Map</h3>
        </Link>

        <Link
          to="/learn"
          className="bg-purple-400 text-white rounded-2xl p-6 shadow hover:scale-[1.02] transition-transform"
        >
          <h3 className="text-xl font-bold">Learn About Hospital</h3>
        </Link>

        <Link
          to="/games"
          className="md:col-span-3 bg-orange-500 text-white rounded-2xl p-6 flex justify-between items-center shadow hover:scale-[1.02] transition-transform"
        >
          <h3 className="text-2xl sm:text-3xl font-bold">Play Zone</h3>
          <span className="text-3xl">üéÆ</span>
        </Link>
      </section>
    </main>
  );
};

export default Dashboard;
