function Sidebar({ handleLogout, isOpen, closeSidebar }) {
  const navigate = useNavigate();

  const logout = () => {
    handleLogout();
    closeSidebar();
    navigate("/login");
  };

  return (
    <>
      {/* Overlay (mobile only) */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black/40 z-30 md:hidden"
          onClick={closeSidebar}
        />
      )}

      <nav
        className={`
          fixed top-0 left-0 h-screen w-64 bg-[#015CE9] rounded-r-3xl shadow-lg
          flex flex-col justify-between pt-6 px-4
          z-40
          transform transition-transform duration-300
          ${isOpen ? "translate-x-0" : "-translate-x-full"}
          md:translate-x-0
        `}
      >
        {/* Close button (mobile only) */}
        <button
          onClick={closeSidebar}
          className="md:hidden text-white text-2xl absolute top-4 right-4"
        >
          âœ•
        </button>

        {/* Top Section */}
        <div>
          {/* Logo */}
          <div className="mb-8 text-center">
            <img src="/logotype.png" alt="logo" className="w-36 mx-auto" />
          </div>

          {/* Menu Links */}
          <ul className="space-y-3 text-white">
            {[
              { to: "/dashboard", label: "Dashboard" },
              { to: "/appointments", label: "Appointments" },
              { to: "/learn", label: "Learn" },
              { to: "/games", label: "Play" },
            ].map((item) => (
              <li key={item.to}>
                <NavLink
                  to={item.to}
                  onClick={closeSidebar}
                  className={({ isActive }) =>
                    `flex items-center px-4 py-2 rounded transition-all
                    ${
                      isActive
                        ? "bg-white text-[#015CE9]"
                        : "text-white hover:bg-blue-700 hover:text-white"
                    }`
                  }
                >
                  {item.label}
                </NavLink>
              </li>
            ))}

            <li>
              <button
                onClick={logout}
                className="w-full text-left px-4 py-2 rounded text-white hover:bg-blue-700 transition-all"
              >
                Logout
              </button>
            </li>
          </ul>
        </div>

        {/* Bottom Image */}
        <div className="mt-6 text-center">
          <img
            src="/assets/images/doctor_sidebar.png"
            alt="Doctor"
            className="w-40 mx-auto"
          />
        </div>
      </nav>
    </>
  );
}
